import React, { useRef, useState } from 'react'
import Footer from '../components/Footer'
import Header from '../components/Header'
import '../styles/addproduct.scss'
import { Link } from 'react-router-dom';

function AddProduct() {

    const [dvdSelected, setDvdSelected] = useState(false);

    const [furnitureSelected, setFurnitureSelected] = useState(false);

    const [bookSelected, setBookSelected] = useState(false);

    const formRef = useRef(null);

    const handleType = ( event ) => {
        switch (event.target.value) {
            case 'DVD':
                setFurnitureSelected(false);
                setBookSelected(false);
                setDvdSelected(true);
                break;
            case 'Furniture':
                setBookSelected(false);
                setDvdSelected(false);
                setFurnitureSelected(true);
                break;
            case 'Book':
                setFurnitureSelected(false);
                setDvdSelected(false);
                setBookSelected(true);
                break;
        
            default:
                setFurnitureSelected(false);
                setDvdSelected(false);
                setBookSelected(false);
                break;
        }
    }

    const save = () => {
        formRef.current.submit();
    }

    const handleSubmit = (event) => {
        console.log(event);
        event.preventDefault();
        alert('hello world')
    }
  
    const lBtn = () => (
        <button className="btn btn-primary" onClick={save}>save</button>
    )
  
    const rBtn = () => (
        <Link to='/'>
            <button className="btn btn-danger">cancel</button>
        </Link>
    )

  return (
    <section className='ap-container'>
        <Header title = 'Product List' leftBtn = {lBtn} rightBtn = {rBtn} />
        <div className="ap-body">
            <form id="#product_form" ref={ formRef } onSubmit = { handleSubmit }>
                <div className="input-wrapper">
                    <label htmlFor="sku">SKU :</label>
                    <input type="text" name='sku' id='sku' required/>
                </div>
                <div className="input-wrapper">
                    <label htmlFor="name">Name :</label>
                    <input type="text" name='name' id='name' required/>
                </div>
                <div className="input-wrapper">
                    <label htmlFor="price">Price :</label>
                    <input type="text" name='price' id='price' required/>
                </div>
                <div className="input-wrapper">
                    <label htmlFor="type">Type :</label>
                    <select name="type" id='productType' onChange={handleType} required>
                        <option value="" hidden >Select product type</option>
                        <option value="DVD">DVD</option>
                        <option value="Furniture">Furniture</option>
                        <option value="Book">Book</option>
                    </select>
                </div>
                { dvdSelected &&
                <div className="input-wrapper">
                    <label htmlFor="size">Size (MB) :</label>
                    <input type="text" name='size' id='size' required/>
                </div>
                }
                { furnitureSelected &&
                <>
                <div className="input-wrapper">
                    <label htmlFor="height">Height (CM) :</label>
                    <input type="text" name='height' id='height' required/>
                </div>
                <div className="input-wrapper">
                    <label htmlFor="width">Width (CM) :</label>
                    <input type="text" name='width' id='width' required/>
                </div>
                <div className="input-wrapper">
                    <label htmlFor="length">Length (CM) :</label>
                    <input type="text" name='length' id='length' required/>
                </div>
                </>
                }
                { bookSelected && 
                <div className="input-wrapper">
                    <label htmlFor="weight">Weight (Kg) :</label>
                    <input type="text" name='weight' id='weight' required/>
                </div>
                }
            </form>
            <div className='logo'>
                Scandiweb
            </div>
        </div>
        <Footer />
    </section>
  )
}

export default AddProduct
