import React, { useEffect, useState } from 'react'
import Footer from '../components/Footer'
import Header from '../components/Header'
import '../styles/products.scss'
import { Link } from 'react-router-dom';
import fetchData from '../hooks/fetchData';
import ProductBox from '../components/ProductBox';

function Products() {

    const [dataLoaded, setDataLoaded] = useState(false);

    const [productList, setProductList] = useState([]);
  
  const massDelete = () => {
      alert('mass delete')
  }

  const lBtn = () => (
      <Link to='/addproduct'>
          <button className="btn btn-primary">add</button>
      </Link>
  )

  const rBtn = () => (
        <button className="btn btn-danger" onClick={massDelete}>mass delete</button>
  )

  useEffect(() => {
    fetchData().then(response => {
        if (response.success) {
            console.log(response);
            setProductList(response.data)
            setDataLoaded(true);
        }
    })
  }, []);

  return (
    <section className='p-container'>
        <Header title = 'Product List' leftBtn = {lBtn} rightBtn = {rBtn}  />
        <div className="p-body">
            {
                dataLoaded ? 
                productList.length ? productList.map( product => (
                    <ProductBox data = { product } key = { product.id }/>
                )) :
                <p className='p-message'>No data</p> :
                <div className="p-message">Loading...</div>
            }
           
        </div>
        <Footer />
    </section>
  )
}

export default Products
