import React from 'react'

function ProductBox( { data , idList , setIdList } ) {
    const handleCheckbox = ( e ) => {
        if(e.target.checked) {
            idList.push(data.id);
            setIdList(idList)
        }
    }
  return (
    <div className="product-wrapper">
        <input type="checkbox" className='delete-checkbox' onChange={ handleCheckbox }/>
        <p>{data.sku}</p>
        <p>{data.name}</p>
        <p>{data.price}$</p>
        <p>{data.type}: {data.value + ( data.type === 'Size' ? ' MB' : data.type === 'Weight' ? ' KG' : '' ) }</p>
    </div>
  )
}

export default ProductBox
