import React from 'react'

function ProductBox( { data , idList , setIdList } ) {
    const handleCheckbox = ( e ) => {
        e.target.className.toggle('delete-checkbox');
        handleList(e.target.checked);
    }

    const handleSelection = (event) => {
        event.currentTarget.classList.toggle('selected');
        const checkbox = event.currentTarget.querySelector('input[type=checkbox]');
        checkbox.className.toggle('delete-checkbox');
        checkbox.checked = !checkbox.checked;
        handleList(checkbox.checked) ;
    }

    const handleList = (checked) => {
        if(checked) {
            idList.push(data.id);
            setIdList(idList);
        }
        else {
            setIdList(idList.filter(id => id !== data.id));
        }
    }

  return (
    <div className="product-wrapper" onClick={ handleSelection }>
        <input type="checkbox" onChange={ handleCheckbox }/>
        <p>{data.sku}</p>
        <p>{data.name}</p>
        <p>{data.price}$</p>
        <p>{data.type}: {data.value + ( data.type === 'Size' ? ' MB' : data.type === 'Weight' ? ' KG' : '' ) }</p>
    </div>
  )
}

export default ProductBox
